import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [token, setToken] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    if (token.trim()) {
      localStorage.setItem("userToken", token);  // simple token save
      navigate("/dashboard");                    // move to dashboard
    }
  };

  return (
    <div className="flex h-screen items-center justify-center bg-[#F7EDE2]">
      <div className="bg-white p-10 rounded-xl shadow-xl w-80">
        <h2 className="text-xl font-bold mb-4 text-center text-[#84A59D]">
          Hospital Portal Login
        </h2>
        <input
          type="text"
          placeholder="Enter Access Token"
          className="w-full p-2 border rounded mb-4"
          value={token}
          onChange={(e) => setToken(e.target.value)}
        />
        <button
          onClick={handleLogin}
          className="w-full bg-[#F28482] text-white py-2 rounded hover:bg-[#ef5b56]"
        >
          Login
        </button>
      </div>
    </div>
  );
}
