import React, { useState } from "react";
import axios from "axios";
import "./Search.css";

export default function Search() {
  const [query, setQuery] = useState("");
  const [messages, setMessages] = useState([]);

  const handleSearch = async () => {
    if (!query.trim()) return;

    const userMessage = { type: "user", text: query };
    setMessages((prev) => [...prev, userMessage]);

    try {
      const res = await axios.post("/api/search", { query });
      const responseMsg = {
        type: "bot",
        results: res.data.results || [],
      };
      setMessages((prev) => [...prev, responseMsg]);
    } catch (err) {
      setMessages((prev) => [...prev, { type: "bot", error: "Search failed." }]);
    }

    setQuery("");
  };

  return (
    <div className="chat__wrapper">
      <div className="chat__header">üìÅ AI-Powered Hospital Search</div>

      <div className="chat__content">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`chat__bubble ${msg.type === "user" ? "user" : "bot"}`}
          >
            {msg.type === "user" ? (
              <p>{msg.text}</p>
            ) : msg.error ? (
              <p className="error">{msg.error}</p>
            ) : (
              <>
                {msg.results.length === 0 ? (
                  <p><em>No results found.</em></p>
                ) : (
                  msg.results.map((r, i) => (
                    <div className="bot__result" key={i}>
                      <strong>{r.title}</strong>
                      <p>{r.snippet}</p>
                      <a href={r.download_url} target="_blank" rel="noreferrer">
                        üìÑ View Document
                      </a>
                      <hr />
                    </div>
                  ))
                )}
              </>
            )}
          </div>
        ))}
      </div>

      <div className="chat__input">
        <input
          type="text"
          placeholder="Ask something..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && handleSearch()}
        />
        <button onClick={handleSearch}>Send</button>
      </div>
    </div>
  );
}
